import React, { useState } from 'react';
import { Navigate, useParams, Link } from 'react-router-dom';
import { createMessage, deletePost } from '../api';

const SendMessage = ({ postID, token }) => {
    const [message, setMessage] = useState({content: ''});
    //we need 3 things to make this request
    //Post-id, token, message object containing the content of the message
    async function addMessage() {
        await createMessage({postID, message, token})
    }
    return (
        <form onSubmit={ (ev) => {
            ev.preventDefault();
            addMessage();
            Navigate('/post');
        }}>
            <input
                type='text'
                placeholder='Enter Message'
                onChange={ (ev) => setMessage({content: ev.target.value}) }
            />
            <button type='submit' onClick={() =>{
                addMessage();
                Navigate('/posts')
            }}>Send Message</button>
        </form>
    )
}

const SinglePostView = ({ posts, token, navigate, getMe }) => {
    const [activeMessage, setActiveMessage] = useState(false);

    const { postID } = useParams();

    if (posts.length) {
    const [currentPost] = posts.filter(post => post._id === postID);
    const {title, description, location, price, willDeliver, updatedAt, createdAt, isAuthor} = currentPost;

    return (
        <div>
            <div>
                <h3>{title}</h3>
                <p>Description: {description}</p>
                <p>Price: {price}</p>
                <p>Location: {location}</p>
                <p>Will Deliver: {willDeliver}</p>
            </div>
            {
                isAuthor ? (
                    <>
                    <Link to='/posts'>
                        <button>
                            Back
                        </button>
                    </Link>
                    <Link to='/posts'>
                        <button 
                        onClick={() => deletePost(token, postID)}>
                            Delete
                        </button>
                    </Link>
                    </>
                ) : (
                    <>
                    <Link to='/posts'>
                        <button>
                            Back
                        </button>
                    </Link>
                    { token &&
                    <>
                    <button 
                    onClick={() => setActiveMessage(!activeMessage)}>
                        Message User
                    </button>
                    {
                        activeMessage && 
                        <SendMessage token={token} 
                        postID={postID} 
                        navigate={navigate} 
                        getMe={getMe} />
                    }
                    </>
                    }
                    </>
                )
            }
            <div>
                <p className='postStamp'>Created At: {createdAt}</p>
                <p className='postStamp'>Updated At: {updatedAt}</p>
            </div>
        </div>
    )
} else {
    return (
        <h2>Waiting for Posts...</h2>
    )
}
}

export default SinglePostView;
