import React, { useState } from 'react';
import { createPost } from '../api';
import { Button, TextField, Card } from '@mui/material';

const CreatePost = ({ token, fetchPosts, navigate }) => {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [price, setPrice] = useState('');
    const [location, setLocation] = useState('');
    const [willDeliver, setWillDeliver] = useState(false);
    const newPost = {
        title,
        description,
        price,
        location,
        willDeliver
    }

    async function addPost() {
        const result = await createPost(token, newPost);
        console.log(result)
        fetchPosts();
        navigate('/posts')
    }

    return (
        //This needs to be a form that accepts the 5 request parameters for creating a post
        <Card>
            <form onSubmit={(event) => {
                event.preventDefault();
                addPost();
            }}>
                <TextField
                    type='text'
                    label="Title"
                    value={title}
                    onChange={(event) => setTitle(event.target.value)}
                />
                <TextField
                    type='text'
                    label="Description"
                    value={description}
                    onChange={(event) => setDescription(event.target.value)}
                />
                <TextField
                    type='text'
                    label="Price"
                    value={price}
                    onChange={(event) => setPrice(event.target.value)}
                />
                <TextField
                    type='text'
                    label="Location"
                    value={location}
                    onChange={(event) => setLocation(event.target.value)}
                />
                <label>Will Deliver</label>
                <input
                    type='checkbox'
                    label="Will Deliver"
                    checked={willDeliver}
                    onChange={(event) => setWillDeliver(!willDeliver)}
                />
                <Button
                    style={{
                        height: '3.5rem',
                        margin: '1rem'
                    }}
                    variant='text'
                    type='submit'
                    onClick={(event) => {
                        event.preventDefault();
                        addPost();
                    }}>Create New Post</Button>
            </form>
        </Card>
    )
}

export default CreatePost;
